mapper.select:
SELECT * FROM ( SELECT A.*, ROW_NUMBER() OVER (ORDER BY bno DESC) AS rn FROM ASBoard A
WHERE status = #{keyword}
WHERE mid LIKE '%'||#{keyword}||'%'
WHERE regdate >= TO_DATE(#{keyword}, 'YYYY-MM-DD')
) WHERE rn <= #{amount} * #{pageNum} AND rn > #{amount} * (#{pageNum} - 1)

SELECT *
FROM (
SELECT
A.*,
ROW_NUMBER() OVER (ORDER BY bno DESC) AS rn
FROM ASBoard A
WHERE mid = #{mid}
)
WHERE rn <= #{amount} * #{pageNum}
AND rn > #{amount} * (#{pageNum} - 1)

mapper.insert:
  	
	
	    SELECT BNO_SEQ.NEXTVAL FROM dual 
	
  	
  	
	INSERT INTO ASBoard (bno, title, content, mid, regDate, updateDate, status)
	    VALUES (#{bno}, #{title}, #{content}, #{mid}, sysdate, sysdate, 
	   
            
                #{status}
            
            
                '답변대기중'
            
        )

	    INSERT INTO ASBoard (bno, title, content, mid, regDate, updateDate, status)
	    VALUES (BNO_SEQ.NEXTVAL, #{title}, #{content}, #{mid}, sysdate, sysdate, 
	    
            
                #{status}
            
            
                '답변대기중'
            
        )

mapper.update:
UPDATE ASBoard
SET title = #{title}, content = #{content}, updateDate = SYSDATE


    , status = #{status}


    , replyContent = #{replyContent}

WHERE bno = #{bno}
