mapper.update:
	UPDATE 	consult
	SET	
      happyCall = #{happyCall}	
      status = #{status}
	WHERE	consultNo = #{consultNo}

mapper.select:
SELECT * FROM ( SELECT c.*, ROW_NUMBER() OVER (ORDER BY consultNo DESC) AS rn FROM consult c
WHERE consultNo = #{keyword}
WHERE buildingType LIKE '%'||#{keyword}||'%'
) WHERE rn <= #{amount} * #{pageNum} AND rn > #{amount} * (#{pageNum} - 1)
