mapper.select:

 	SELECT rownum as odRn, odNo, odTitle, odContent, odName, odDate, odPlace, odTime, odPeople, odMc, odDeadline, odState, odWDate, mid, odImg
	FROM oneday 
	WHERE odNo = #{odNo}

mapper.update:
UPDATE oneday
odTitle = #{odTitle}, odContent = #{odContent}, odName = #{odName}, odDate = #{odDate}, odPlace = #{odPlace}, odTime = #{odTime}, odPeople = #{odPeople}, odMc = #{odMc}, odDeadline = #{odDeadline}, odState = #{odState}, odImg = #{odImg},
WHERE odNo = #{odNo}
